INSERT INTO `questions` (`id`, `testid`, `vapros`, `a`, `b`, `c`, `otg`, `avtor`) VALUES
(11, 8, 'Коя река е образувала Кресненския пролом?', 'Струма', 'Тунджа', 'Места', 'a', ''),
(12, 8, 'Коя от реките НЕ е от Черноморската отточна област?', 'Янтра', 'Искър', 'Въча', 'c', ''),
(13, 8, 'Най- високият връх на планината Осогово е: ', 'вр. Богдан', 'вр. Руен', 'вр. Радомир', 'b', ''),
(14, 8, 'Коя от пещерите се намира в Западните Родопи? ', 'Орлова чука', 'Ягодинската пещера', 'Магура', 'b', ''),
(15, 8, 'Кой от изброените обекти НЕ се намира в Краище? ', 'Ихтиманската котловина', 'Руй', 'Голо бърдо', 'a', ''),
(16, 8, 'Рабишкото езеро по произход е: ', 'свлачищно', 'тектонско', 'крайморско', 'b', ''),
(17, 8, 'Белоградчишките скали се намират в планината: ', 'Западни Родопи', 'Предбалкан', 'Пирин', 'b', ''),
(18, 8, 'За коя планина се отнася твърдението: „ Планина с куполовидна форма, каменни реки и с карстови форми, между които е най-дългата пе', 'Витоша', 'Рила', 'Стара планина', 'a', ''),
(19, 8, 'Коя част от територията на България е най-бедна на водни ресурси?', 'Добруджа', 'Предбалкан', 'Средна гора', 'a', ''),
(20, 8, 'На коя река е изграден язовир „ Тича”? ', 'Арда', 'Тунджа', 'Камчия', 'c', ''),
(21, 8, 'Къде се намира най-късно загасналият вулкан в България-Кожух? ', 'в долината на р. Места', 'в долината на р. Струма', 'в Родопите', 'b', ''),
(22, 8, 'Къде са разположени скалните феномени „ Вкаменената сватба”, „ Чудните мостове”, пирамидите при селата Зимзелен и Доброволец? ', 'в Рила', 'в Пирин', 'в Родопите', 'c', ''),
(23, 8, 'Коя от българските реки е с най-голям водосборен басейн?', 'Марица', 'Арда', 'Искър', 'a', ''),
(24, 8, 'В коя планина се намират красивите водопади-Райското пръскало и Видимското пръскало?', 'в Средна Стара планина', 'в Рила и Пирин', 'в Родопите', 'a', ''),
(25, 8, 'Къде се намират Ягодинската пещера, Дяволското гърло, Снежанка и Ухловица?', 'в Стара планина', 'в Родопите', 'в Дунавската равнина', 'b', ''),
(26, 8, 'Най-горещият минерален извор на територията на България е: ', 'Левуново', 'Меричлери', 'Сапарева баня', 'c', ''),
(27, 8, 'Кои са най-разпространенте широколистни дървестни видове в България? ', 'дъб и бук', 'липа и габър', 'топола и бряст', 'a', ''),
(28, 8, 'Лонгозните гори са разпространени по:', 'дол. на реките Вит, Осъм и Янтра', 'речните устия на реките Камчия, Ропотамо', 'горните течения на реките Струма, Места ', 'b', ''),
(29, 8, 'Кои от посочените растителни видове са реликтни?', 'чинар, лавровишня, бяла и черна мура', 'бял и черен бор, смърч и ела', 'бръшлян, острица, житняк, коило', 'a', ''),
(30, 8, 'Кой е първият биосферен резерват, изграден в България? ', 'Ропотамо', 'Парангалица', 'Силкосия', 'c', ''),
(31, 8, 'Кой е първият национален парк в България и на Балканския полуостров, обявен за такъв през 1934 г.? ', 'Централен Балкан', 'Рила', 'Витоша', 'c', '');

